        dataEntrada: initialData.dataEntrada,
        departamento: initialData.departamento,
        alertas: initialData.alertas,
      });
    } else {
      // Se for novo cadastro, limpa o formulrio
      reset({
        codigoProduto: '',
        nome: '',
        unidade: 'kg',
        lote: '',
        dataFabricacao: '',
        dataValidade: '',
