import { useQuery } from '@tanstack/react-query';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { Badge } from '@/components/ui/badge';
import { ScrollArea } from '@/components/ui/scroll-area';
import { Calendar, User, FileText } from 'lucide-react';
import type { AuditLog } from '@shared/schema';
import { format } from 'date-fns';
import { ptBR } from 'date-fns/locale';

interface AuditLogDialogProps {
  open: boolean;
  onClose: () => void;
}

function getActionLabel(action: string): string {
  const labels: Record<string, string> = {
    CREATE: 'Cadastro',
    UPDATE: 'Edio',
    DELETE: 'Excluso',
    SAIDA: 'Sada',
  };
  return labels[action] || action;
}

function getActionColor(action: string): string {
  const colors: Record<string, string> = {
    CREATE: 'default',
    UPDATE: 'secondary',
    DELETE: 'destructive',
    SAIDA: 'outline',
  };
  return colors[action] || 'default';
}

export function AuditLogDialog({ open, onClose }: AuditLogDialogProps) {
  const { data: logs = [], isLoading } = useQuery<AuditLog[]>({
    queryKey: ['/api/audit-log'],
    enabled: open,
  });

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-3xl max-h-[90vh]">
        <DialogHeader>
          <DialogTitle>Histrico de Auditoria</DialogTitle>
          <DialogDescription>
            Visualize todas as operaes realizadas no sistema (cadastros, edies, excluses e sadas)
          </DialogDescription>
        </DialogHeader>

        <ScrollArea className="h-[500px] pr-4">
          {isLoading ? (
            <div className="flex items-center justify-center py-12">
              <div className="text-center">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-2"></div>
                <p className="text-sm text-muted-foreground">Carregando histrico...</p>
              </div>
            </div>
          ) : logs.length === 0 ? (
            <div className="flex items-center justify-center py-12">
              <p className="text-muted-foreground">Nenhum registro de auditoria encontrado</p>
            </div>
          ) : (
            <div className="space-y-4">
              {logs.map((log) => (
                <div
                  key={log.id}
                  data-testid={`audit-log-${log.id}`}
                  className="border rounded-lg p-4 hover-elevate"
                >
                  <div className="flex items-start justify-between gap-4 mb-3">
                    <div className="flex items-center gap-2">
                      <Badge variant={getActionColor(log.action) as any} data-testid={`badge-action-${log.id}`}>
                        {getActionLabel(log.action)}
                      </Badge>
                      {log.alimentoNome && (
                        <span className="font-semibold" data-testid={`text-alimento-${log.id}`}>
                          {log.alimentoNome}
                        </span>
                      )}
                    </div>
                    <div className="flex items-center gap-2 text-xs text-muted-foreground">
                      <Calendar className="h-3 w-3" />
                      {format(new Date(log.timestamp), "dd/MM/yyyy 's' HH:mm", { locale: ptBR })}
                    </div>
                  </div>

                  <div className="grid gap-2 text-sm">
                    {log.alimentoCodigo && (
                      <div className="flex items-center gap-2">
                        <FileText className="h-4 w-4 text-muted-foreground" />
                        <span className="text-muted-foreground">Cdigo:</span>
                        <span className="font-mono">{log.alimentoCodigo}</span>
                      </div>
                    )}

                    <div className="flex items-center gap-2">
                      <User className="h-4 w-4 text-muted-foreground" />
                      <span className="text-muted-foreground">Usurio:</span>
                      <span>{log.userName}</span>
                    </div>

                    {log.changes && typeof log.changes === 'object' && Object.keys(log.changes as Record<string, any>).length > 0 && (
                      <div className="mt-2 p-3 bg-muted rounded-md">
                        <p className="text-xs font-semibold text-muted-foreground mb-2">Detalhes:</p>
                        <div className="space-y-1 text-xs">
                          {Object.entries(log.changes as Record<string, any>).map(([key, value]) => (
                            <div key={key} className="flex gap-2">
                              <span className="text-muted-foreground">{key}:</span>
                              <span className="font-mono">{JSON.stringify(value)}</span>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </ScrollArea>
      </DialogContent>
    </Dialog>
  );
}

