import { sql } from "drizzle-orm";
import { pgTable, text, varchar, integer, timestamp, boolean, real, jsonb } from "drizzle-orm/pg-core";
import { relations } from "drizzle-orm";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Tabela de usurios
export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  nome: text("nome").notNull(),
  email: text("email").notNull().unique(),
  password: text("password").notNull(),
  resetToken: text("reset_token"),
  resetTokenExpiry: timestamp("reset_token_expiry"),
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Tabela de alimentos - REMOVIDO campo departamento
export const alimentos = pgTable("alimentos", {
  id: integer("id").primaryKey().generatedAlwaysAsIdentity(),
  codigoProduto: text("codigo_produto").notNull(),
  nome: text("nome").notNull(),
  unidade: text("unidade").notNull(), // 'kg' ou 'caixa'
  lote: text("lote").notNull(),
  dataFabricacao: text("data_fabricacao").notNull(),
  dataValidade: text("data_validade").notNull(),
  quantidade: real("quantidade").notNull().default(0),
  pesoPorCaixa: real("peso_por_caixa"),
  temperatura: text("temperatura").notNull(),
  shelfLife: integer("shelf_life").notNull(),
  dataEntrada: text("data_entrada").notNull(),
  dataSaida: text("data_saida"),
  // Configuraes de alertas como JSON
  alertasConfig: jsonb("alertas_config").notNull().$type<{
    contarAPartirFabricacaoDias: number;
    avisoQuandoUmTercoValidade: boolean;
    popUpNotificacoes: boolean;
  }>(),
  // Metadata
  cadastradoPor: varchar("cadastrado_por").notNull().references(() => users.id),
  createdAt: timestamp("created_at").defaultNow().notNull(),
  updatedAt: timestamp("updated_at").defaultNow().notNull(),
});

// Tabela de modelos de produtos (template) - baseado na planilha Excel
export const modelosProdutos = pgTable("modelos_produtos", {
  id: integer("id").primaryKey().generatedAlwaysAsIdentity(),
  codigoProduto: text("codigo_produto").notNull().unique(), // Z06_COD
  descricao: text("descricao").notNull(), // Z06_DESC
  temperatura: text("temperatura").notNull(), // Z06_ARMA
  shelfLife: integer("shelf_life").notNull(), // Z06_PRAZO
  gtin: text("gtin"), // Z06_GTIN - cdigo de barras
  pesoEmbalagem: real("peso_embalagem"), // Z06_TREMB
  pesoPorCaixa: real("peso_por_caixa"), // Z06_TRCX
  empresa: text("empresa"), // Z06_EMPRE
  pesoLiquido: real("peso_liquido"), // Z06_PESOLI
  tipoPeso: text("tipo_peso"), // Z06_TPPESO (V ou F)
  quantidadePorCaixa: integer("quantidade_por_caixa"), // Z06_QTCX
  unidadePadrao: text("unidade_padrao").notNull().default("kg"), // 'kg' ou 'caixa'
  createdAt: timestamp("created_at").defaultNow().notNull(),
});

// Tabela de auditoria para rastrear TODAS as operaes
export const auditLog = pgTable("audit_log", {
  id: integer("id").primaryKey().generatedAlwaysAsIdentity(),
  alimentoId: integer("alimento_id"), // Pode ser null se o alimento foi deletado
  alimentoCodigo: text("alimento_codigo"), // Guardamos o cdigo do produto para referncia
  alimentoNome: text("alimento_nome"), // Nome do alimento para referncia
  action: text("action").notNull(), // 'CREATE', 'UPDATE', 'DELETE', 'SAIDA'
  userId: varchar("user_id").notNull().references(() => users.id),
  userName: text("user_name").notNull(),
  changes: jsonb("changes"), // Detalhes das mudanas
  timestamp: timestamp("timestamp").defaultNow().notNull(),
});

// Relaes
export const usersRelations = relations(users, ({ many }) => ({
  alimentos: many(alimentos),
  auditLogs: many(auditLog),
}));

export const alimentosRelations = relations(alimentos, ({ one, many }) => ({
  cadastradoPor: one(users, {
    fields: [alimentos.cadastradoPor],
    references: [users.id],
  }),
  auditLogs: many(auditLog),
}));

export const auditLogRelations = relations(auditLog, ({ one }) => ({
  user: one(users, {
    fields: [auditLog.userId],
    references: [users.id],
  }),
}));

// Schemas de insero com validao de email REAL
export const insertUserSchema = createInsertSchema(users, {
  email: z.string().email("Digite um email vlido").min(5, "Email deve ter pelo menos 5 caracteres"),
  password: z.string().min(6, "Senha deve ter pelo menos 6 caracteres"),
  nome: z.string().min(2, "Nome deve ter pelo menos 2 caracteres"),
}).omit({
  id: true,
  createdAt: true,
  resetToken: true,
  resetTokenExpiry: true,
});

// Schema de modelo de produto
const baseModeloProdutoSchema = createInsertSchema(modelosProdutos).omit({
  id: true,
  createdAt: true,
});

export const insertModeloProdutoSchema = baseModeloProdutoSchema.extend({
  codigoProduto: z.string().min(1, "Cdigo do produto  obrigatrio"),
  descricao: z.string().min(1, "Descrio  obrigatria"),
  temperatura: z.string().min(1, "Temperatura  obrigatria"),
  shelfLife: z.number().min(1, "Shelf life deve ser maior que 0"),
  gtin: z.string().nullable().optional(),
  pesoEmbalagem: z.number().nullable().optional(),
  pesoPorCaixa: z.number().nullable().optional(),
  empresa: z.string().nullable().optional(),
  pesoLiquido: z.number().nullable().optional(),
  tipoPeso: z.string().nullable().optional(),
  quantidadePorCaixa: z.number().nullable().optional(),
  unidadePadrao: z.enum(["kg", "caixa"], { errorMap: () => ({ message: "Unidade deve ser kg ou caixa" }) }),
});

// Schema de alimento SEM departamento
const baseAlimentoSchema = createInsertSchema(alimentos).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
  cadastradoPor: true,
});

export const insertAlimentoSchema = baseAlimentoSchema.extend({
  codigoProduto: z.string().min(1, "Cdigo do produto  obrigatrio"),
  nome: z.string().min(1, "Nome  obrigatrio"),
  unidade: z.enum(["kg", "caixa"], { errorMap: () => ({ message: "Unidade deve ser kg ou caixa" }) }),
  lote: z.string().min(1, "Lote  obrigatrio"),
  dataFabricacao: z.string().min(1, "Data de fabricao  obrigatria"),
  dataValidade: z.string().min(1, "Data de validade  obrigatria"),
  quantidade: z.number().min(0, "Quantidade deve ser maior ou igual a 0"),
  pesoPorCaixa: z.number().nullable().optional(),
  temperatura: z.string().min(1, "Temperatura  obrigatria"),
  shelfLife: z.number().min(1, "Shelf life deve ser maior que 0"),
  dataEntrada: z.string().optional(),
  dataSaida: z.string().nullable().optional(),
  alertasConfig: z.object({
    contarAPartirFabricacaoDias: z.number(),
    avisoQuandoUmTercoValidade: z.boolean(),
    popUpNotificacoes: z.boolean(),
  }),
});

const baseAuditLogSchema = createInsertSchema(auditLog).omit({
  id: true,
  timestamp: true,
});

export const insertAuditLogSchema = baseAuditLogSchema.extend({
  action: z.string().min(1, "Ao  obrigatria"),
  userId: z.string().min(1, "ID do usurio  obrigatrio"),
  userName: z.string().min(1, "Nome do usurio  obrigatrio"),
  alimentoId: z.number().nullable().optional(),
  alimentoCodigo: z.string().nullable().optional(),
  alimentoNome: z.string().nullable().optional(),
  changes: z.any().nullable().optional(),
});

// Tipos TypeScript
export type User = typeof users.$inferSelect;
export type InsertUser = z.infer<typeof insertUserSchema>;

export type ModeloProduto = typeof modelosProdutos.$inferSelect;
export type InsertModeloProduto = z.infer<typeof insertModeloProdutoSchema>;

export type Alimento = typeof alimentos.$inferSelect;
export type InsertAlimento = z.infer<typeof insertAlimentoSchema>;

export type AuditLog = typeof auditLog.$inferSelect;
export type InsertAuditLog = z.infer<typeof insertAuditLogSchema>;

// Tipos para o frontend (com campos computados)
export interface AlimentoComputado extends Alimento {
  dataInspecao: string;
  status: 'VENCIDO' | 'VENCE EM BREVE' | 'ATIVO';
  alerta: 'AMARELO' | 'NENHUM';
  diasRestantes: number;
  pesoTotal: number;
  nomeUsuario?: string;
}

// Schema para login
export const loginSchema = z.object({
  email: z.string().email("Digite um email vlido"),
  password: z.string().min(1, "Senha  obrigatria"),
});

export type LoginData = z.infer<typeof loginSchema>;

// Schema para recuperao de senha
export const forgotPasswordSchema = z.object({
  email: z.string().email("Digite um email vlido"),
});

export type ForgotPasswordData = z.infer<typeof forgotPasswordSchema>;

// Schema para resetar senha
export const resetPasswordSchema = z.object({
  token: z.string().min(1, "Token  obrigatrio"),
  newPassword: z.string().min(6, "Nova senha deve ter pelo menos 6 caracteres"),
});

export type ResetPasswordData = z.infer<typeof resetPasswordSchema>;

