import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Label } from '@/components/ui/label';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { useToast } from '@/hooks/use-toast';
import { Upload, FileSpreadsheet, CheckCircle2, AlertCircle } from 'lucide-react';
import { useMutation } from '@tanstack/react-query';
import { apiRequest, queryClient } from '@/lib/queryClient';
import * as XLSX from 'xlsx';
import type { InsertModeloProduto } from '@shared/schema';

interface ImportModelosDialogProps {
  open: boolean;
  onClose: () => void;
}

export function ImportModelosDialog({ open, onClose }: ImportModelosDialogProps) {
  const { toast } = useToast();
  const [file, setFile] = useState<File | null>(null);
  const [preview, setPreview] = useState<any[]>([]);
  const [processedData, setProcessedData] = useState<InsertModeloProduto[]>([]);
  const [errors, setErrors] = useState<string[]>([]);

  const importMutation = useMutation({
    mutationFn: async (modelos: InsertModeloProduto[]) => {
      const response = await apiRequest('POST', '/api/modelos-produtos/import-excel', { modelos });
      return response;
    },
    onSuccess: (data: any) => {
      queryClient.invalidateQueries({ queryKey: ['/api/modelos-produtos'] });
      toast({
        title: 'Importao concluda',
        description: `${data.imported} modelos importados, ${data.updated} atualizados!`,
      });
      handleClose();
    },
    onError: (error: any) => {
      toast({
        title: 'Erro na importao',
        description: error.message || 'Erro ao importar modelos de produtos',
        variant: 'destructive',
      });
    },
  });

  const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFile = e.target.files?.[0];
    if (!selectedFile) return;

    setFile(selectedFile);
    setErrors([]);

    try {
      const data = await selectedFile.arrayBuffer();
      const workbook = XLSX.read(data);
      const sheetName = workbook.SheetNames[0];
      const worksheet = workbook.Sheets[sheetName];
      const jsonData = XLSX.utils.sheet_to_json(worksheet);

      const processedData: InsertModeloProduto[] = [];
      const validationErrors: string[] = [];

      jsonData.forEach((row: any, index: number) => {
        try {
          const modelo: InsertModeloProduto = {
            codigoProduto: String(row['Z06_COD'] || '').trim(),
            descricao: String(row['Z06_DESC'] || '').trim(),
            temperatura: String(row['Z06_ARMA'] || '').trim(),
            shelfLife: Number(row['Z06_PRAZO'] || 0),
            gtin: row['Z06_GTIN'] ? String(row['Z06_GTIN']).trim() : null,
            pesoEmbalagem: row['Z06_TREMB'] ? Number(row['Z06_TREMB']) : null,
            pesoPorCaixa: row['Z06_TRCX'] ? Number(row['Z06_TRCX']) : null,
            empresa: row['Z06_EMPRE'] ? String(row['Z06_EMPRE']).trim() : null,
            pesoLiquido: row['Z06_PESOLI'] ? Number(row['Z06_PESOLI']) : null,
            tipoPeso: row['Z06_TPPESO'] ? String(row['Z06_TPPESO']).trim() : null,
            quantidadePorCaixa: row['Z06_QTCX'] ? Number(row['Z06_QTCX']) : null,
            unidadePadrao: 'kg',
          };

          if (!modelo.codigoProduto || !modelo.descricao || !modelo.temperatura || !modelo.shelfLife) {
            validationErrors.push(`Linha ${index + 2}: Faltam campos obrigatrios (Cdigo, Descrio, Temperatura ou Prazo)`);
          } else {
            processedData.push(modelo);
          }
        } catch (error) {
          validationErrors.push(`Linha ${index + 2}: Erro ao processar dados`);
        }
      });

      setProcessedData(processedData);
      setPreview(processedData.slice(0, 5));
      setErrors(validationErrors);

      if (validationErrors.length > 0) {
        toast({
          title: 'Avisos na importao',
          description: `${validationErrors.length} linhas com problemas`,
          variant: 'default',
        });
      }

      if (processedData.length > 0) {
        toast({
          title: 'Arquivo processado',
          description: `${processedData.length} modelos prontos para importar`,
        });
      }
    } catch (error) {
      toast({
        title: 'Erro ao ler arquivo',
        description: 'Verifique se o arquivo  um Excel vlido no formato esperado',
        variant: 'destructive',
      });
    }
  };

  const handleImport = () => {
    if (processedData.length > 0) {
      importMutation.mutate(processedData);
    }
  };

  const handleClose = () => {
    setFile(null);
    setPreview([]);
    setProcessedData([]);
    setErrors([]);
    onClose();
  };

  return (
    <Dialog open={open} onOpenChange={handleClose}>
      <DialogContent className="max-w-3xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Importar Modelos de Produtos via Excel</DialogTitle>
          <DialogDescription>
            Faa upload de um arquivo Excel (.xlsx, .xls) com os modelos de produtos.
            <br />
            <span className="text-sm font-medium mt-2 block">
              O arquivo deve conter as colunas: Z06_COD, Z06_DESC, Z06_ARMA, Z06_PRAZO, etc.
            </span>
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          <div className="border-2 border-dashed border-muted-foreground/25 rounded-lg p-8 text-center hover-elevate">
            <div className="flex flex-col items-center gap-4">
              <FileSpreadsheet className="h-12 w-12 text-muted-foreground" />
              <div>
                <Label htmlFor="file-upload-modelos" className="cursor-pointer">
                  <div className="text-base font-medium text-primary hover:underline">
                    Clique para selecionar um arquivo
                  </div>
                  <div className="text-sm text-muted-foreground mt-1">
                    Aceita arquivos .xlsx e .xls (Formato Protheus)
                  </div>
                </Label>
                <input
                  data-testid="input-file-upload-modelos"
                  id="file-upload-modelos"
                  type="file"
                  accept=".xlsx,.xls"
                  onChange={handleFileChange}
                  className="hidden"
                />
              </div>
              {file && (
                <div className="flex items-center gap-2 text-sm">
                  <CheckCircle2 className="h-4 w-4 text-green-600" />
                  <span className="font-medium">{file.name}</span>
                </div>
              )}
            </div>
          </div>

          {errors.length > 0 && (
            <div className="rounded-lg bg-destructive/10 border border-destructive/20 p-4">
              <div className="flex items-start gap-2">
                <AlertCircle className="h-5 w-5 text-destructive mt-0.5" />
                <div className="flex-1">
                  <h4 className="font-semibold text-destructive mb-2">Problemas encontrados:</h4>
                  <ul className="text-sm space-y-1">
                    {errors.slice(0, 5).map((error, i) => (
                      <li key={i}> {error}</li>
                    ))}
                    {errors.length > 5 && (
                      <li className="text-muted-foreground">
                        ... e mais {errors.length - 5} problemas
                      </li>
                    )}
                  </ul>
                </div>
              </div>
            </div>
          )}

          {preview.length > 0 && (
            <div>
              <h4 className="font-semibold mb-2">Preview ({preview.length} modelos):</h4>
              <div className="border rounded-lg overflow-hidden">
                <div className="overflow-x-auto">
                  <table className="w-full text-sm">
                    <thead className="bg-muted">
                      <tr>
                        <th className="px-3 py-2 text-left">Cdigo</th>
                        <th className="px-3 py-2 text-left">Descrio</th>
                        <th className="px-3 py-2 text-left">Temperatura</th>
                        <th className="px-3 py-2 text-left">Shelf (dias)</th>
                      </tr>
                    </thead>
                    <tbody>
                      {preview.map((item, i) => (
                        <tr key={i} className="border-t">
                          <td className="px-3 py-2 font-mono text-xs">{item.codigoProduto}</td>
                          <td className="px-3 py-2">{item.descricao}</td>
                          <td className="px-3 py-2 text-xs">{item.temperatura}</td>
                          <td className="px-3 py-2">{item.shelfLife}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          )}

          <div className="flex justify-end gap-3 pt-4">
            <Button data-testid="button-cancel-import-modelos" variant="outline" onClick={handleClose}>
              Cancelar
            </Button>
            <Button
              data-testid="button-confirm-import-modelos"
              onClick={handleImport}
              disabled={processedData.length === 0 || importMutation.isPending}
            >
              {importMutation.isPending ? 'Importando...' : `Importar ${processedData.length} modelos`}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

