import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import session from "express-session";
import createMemoryStore from "memorystore";
import bcrypt from "bcryptjs";
import crypto from "crypto";
import { insertUserSchema, loginSchema, forgotPasswordSchema, resetPasswordSchema, insertAlimentoSchema, insertModeloProdutoSchema, type User } from "@shared/schema";

const MemoryStore = createMemoryStore(session);

// Extend Express session with user
declare module 'express-session' {
  interface SessionData {
    userId?: string;
  }
}

export async function registerRoutes(app: Express): Promise<Server> {
  // Session middleware
  app.use(
    session({
      secret: process.env.SESSION_SECRET || 'shelf-aid-secret-key-change-in-production',
      resave: false,
      saveUninitialized: false,
      store: new MemoryStore({
        checkPeriod: 86400000, // 24 hours
      }),
      cookie: {
        maxAge: 7 * 24 * 60 * 60 * 1000, // 7 days
        httpOnly: true,
        secure: process.env.NODE_ENV === 'production',
        sameSite: 'lax',
      },
    })
  );

  // Middleware para verificar autenticao
  const requireAuth = async (req: any, res: any, next: any) => {
    if (!req.session.userId) {
      return res.status(401).json({ message: 'No autenticado' });
    }
    const user = await storage.getUser(req.session.userId);
    if (!user) {
      return res.status(401).json({ message: 'Usurio no encontrado' });
    }
    req.user = user;
    next();
  };

  // ============ AUTENTICAO ============

  // Registrar novo usurio
  app.post('/api/auth/register', async (req, res) => {
    try {
      const data = insertUserSchema.parse(req.body);

      // Verificar se email j existe
      const existingUser = await storage.getUserByEmail(data.email);
      if (existingUser) {
        return res.status(400).json({ message: 'Email j cadastrado' });
      }

      // Hash da senha
      const hashedPassword = await bcrypt.hash(data.password, 10);

      // Criar usurio
      const user = await storage.createUser({
        ...data,
        password: hashedPassword,
      });

      // Fazer login automtico
      req.session.userId = user.id;

      res.json({
        id: user.id,
        nome: user.nome,
        email: user.email,
        createdAt: user.createdAt,
      });
    } catch (error: any) {
      console.error('Erro no registro:', error);
      res.status(400).json({ message: error.message || 'Erro ao registrar usurio' });
    }
  });

  // Login
  app.post('/api/auth/login', async (req, res) => {
    try {
      const data = loginSchema.parse(req.body);

      const user = await storage.getUserByEmail(data.email);
      if (!user) {
        return res.status(401).json({ message: 'Email ou senha incorretos' });
      }

      const validPassword = await bcrypt.compare(data.password, user.password);
      if (!validPassword) {
        return res.status(401).json({ message: 'Email ou senha incorretos' });
      }

      req.session.userId = user.id;

      res.json({
        id: user.id,
        nome: user.nome,
        email: user.email,
        createdAt: user.createdAt,
      });
    } catch (error: any) {
      console.error('Erro no login:', error);
      res.status(400).json({ message: error.message || 'Erro ao fazer login' });
    }
  });

  // Logout
  app.post('/api/auth/logout', (req, res) => {
    req.session.destroy((err) => {
      if (err) {
        return res.status(500).json({ message: 'Erro ao fazer logout' });
      }
      res.json({ message: 'Logout realizado com sucesso' });
    });
  });

  // Obter usurio atual
  app.get('/api/auth/me', requireAuth, (req: any, res) => {
    const { password, resetToken, resetTokenExpiry, ...userWithoutPassword } = req.user;
    res.json(userWithoutPassword);
  });

  // Esqueci minha senha (gera token e envia email via Supabase)
  app.post('/api/auth/forgot-password', async (req, res) => {
    try {
      const data = forgotPasswordSchema.parse(req.body);

      const user = await storage.getUserByEmail(data.email);
      if (!user) {
        return res.json({ message: 'Se o email existir, voc receber instrues de recuperao' });
      }

      const resetToken = crypto.randomBytes(32).toString('hex');
      const resetTokenExpiry = new Date(Date.now() + 3600000);

      await storage.updateUser(user.id, {
        resetToken,
        resetTokenExpiry,
      });

      const resetUrl = `${process.env.REPLIT_DEV_DOMAIN ? `https://${process.env.REPLIT_DEV_DOMAIN}` : 'http://localhost:5173'}/reset-password?token=${resetToken}`;

      console.log('');
      console.log(' RECUPERAO DE SENHA');
      console.log('');
      console.log('Email:', user.email);
      console.log('Token:', resetToken);
      console.log('Link:', resetUrl);
      console.log('');
      console.log('NOTA: Configure um servio de email (SendGrid, AWS SES, etc)');
      console.log('      para enviar emails em produo.');
      console.log('');

      res.json({
        message: 'Token de recuperao gerado. Em desenvolvimento, verifique o console do servidor.',
        resetToken: process.env.NODE_ENV !== 'production' ? resetToken : undefined,
        resetUrl: process.env.NODE_ENV !== 'production' ? resetUrl : undefined,
      });
    } catch (error: any) {
      console.error('Erro ao solicitar recuperao:', error);
      res.status(400).json({ message: error.message || 'Erro ao solicitar recuperao de senha' });
    }
  });

  // Resetar senha com token
  app.post('/api/auth/reset-password', async (req, res) => {
    try {
      const data = resetPasswordSchema.parse(req.body);

      const allUsers = await storage.getAllUsers();
      const user = allUsers.find(u => u.resetToken === data.token);

      if (!user || !user.resetTokenExpiry) {
        return res.status(400).json({ message: 'Token invlido ou expirado' });
      }

      if (new Date() > new Date(user.resetTokenExpiry)) {
        return res.status(400).json({ message: 'Token expirado. Solicite uma nova recuperao de senha' });
      }

      const hashedPassword = await bcrypt.hash(data.newPassword, 10);

      await storage.updateUser(user.id, {
        password: hashedPassword,
        resetToken: null,
        resetTokenExpiry: null,
      });

      res.json({ message: 'Senha redefinida com sucesso! Voc j pode fazer login com a nova senha.' });
    } catch (error: any) {
      console.error('Erro ao resetar senha:', error);
      res.status(400).json({ message: error.message || 'Erro ao resetar senha' });
    }
  });

  // ============ MODELOS DE PRODUTOS ============

  // Listar todos os modelos de produtos
  app.get('/api/modelos-produtos', requireAuth, async (req, res) => {
    try {
      const modelos = await storage.getAllModelosProdutos();
      res.json(modelos);
    } catch (error: any) {
      console.error('Erro ao listar modelos:', error);
      res.status(500).json({ message: 'Erro ao listar modelos de produtos' });
    }
  });

  // Buscar modelo de produto por cdigo
  app.get('/api/modelos-produtos/:codigo', requireAuth, async (req, res) => {
    try {
      const modelo = await storage.getModeloProdutoByCodigo(req.params.codigo);
      if (!modelo) {
        return res.status(404).json({ message: 'Modelo no encontrado' });
      }
      res.json(modelo);
    } catch (error: any) {
      console.error('Erro ao buscar modelo:', error);
      res.status(500).json({ message: 'Erro ao buscar modelo de produto' });
    }
  });

  // Criar modelo de produto
  app.post('/api/modelos-produtos', requireAuth, async (req, res) => {
    try {
      const data = insertModeloProdutoSchema.parse(req.body);
      const modelo = await storage.createModeloProduto(data);
      res.json(modelo);
    } catch (error: any) {
      console.error('Erro ao criar modelo:', error);
      res.status(400).json({ message: error.message || 'Erro ao criar modelo de produto' });
    }
  });

  // Importar modelos de produtos de planilha Excel
  app.post('/api/modelos-produtos/import-excel', requireAuth, async (req, res) => {
    try {
      const { modelos } = req.body;

      if (!Array.isArray(modelos)) {
        return res.status(400).json({ message: 'Formato invlido' });
      }

      let imported = 0;
      let updated = 0;
      const errors: string[] = [];

      for (const modeloData of modelos) {
        try {
          const data = insertModeloProdutoSchema.parse(modeloData);

          const existente = await storage.getModeloProdutoByCodigo(data.codigoProduto);

          if (existente) {
            await storage.updateModeloProduto(existente.id, data);
            updated++;
          } else {
            await storage.createModeloProduto(data);
            imported++;
          }
        } catch (error: any) {
          const codigo = (modeloData as any).codigoProduto || 'desconhecido';
          errors.push(`Erro ao importar cdigo ${codigo}: ${error.message}`);
        }
      }

      res.json({ imported, updated, errors });
    } catch (error: any) {
      console.error('Erro na importao de modelos:', error);
      res.status(500).json({ message: 'Erro na importao de modelos' });
    }
  });

  // ============ ALIMENTOS ============

  // Listar todos os alimentos
  app.get('/api/alimentos', requireAuth, async (req, res) => {
    try {
      const alimentos = await storage.getAllAlimentos();
      res.json(alimentos);
    } catch (error: any) {
      console.error('Erro ao listar alimentos:', error);
      res.status(500).json({ message: 'Erro ao listar alimentos' });
    }
  });

  // Criar alimento
  app.post('/api/alimentos', requireAuth, async (req: any, res) => {
    try {
      const data = insertAlimentoSchema.parse(req.body);

      // IMPORTANTE: Data de entrada sempre vem do servidor (data atual)
      const alimentoData = {
        ...data,
        dataEntrada: new Date().toISOString().split('T')[0],
        dataSaida: undefined, // Sada s  definida quando registrada
      };

      const alimento = await storage.createAlimento(alimentoData, req.user.id);

      // Registrar no audit log
      await storage.createAuditLog({
        alimentoId: alimento.id,
        alimentoCodigo: alimento.codigoProduto,
        alimentoNome: alimento.nome,
        action: 'CREATE',
        userId: req.user.id,
        userName: req.user.nome,
        changes: { alimento: data },
      });

      res.json(alimento);
    } catch (error: any) {
      console.error('Erro ao criar alimento:', error);
      res.status(400).json({ message: error.message || 'Erro ao criar alimento' });
    }
  });

  // Atualizar alimento
  app.patch('/api/alimentos/:id', requireAuth, async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      const data = insertAlimentoSchema.partial().parse(req.body);

      const alimentoAntes = await storage.getAlimento(id);
      if (!alimentoAntes) {
        return res.status(404).json({ message: 'Alimento no encontrado' });
      }

      const alimento = await storage.updateAlimento(id, data);

      // Registrar no audit log
      await storage.createAuditLog({
        alimentoId: id,
        alimentoCodigo: alimento?.codigoProduto || alimentoAntes.codigoProduto,
        alimentoNome: alimento?.nome || alimentoAntes.nome,
        action: 'UPDATE',
        userId: req.user.id,
        userName: req.user.nome,
        changes: { antes: alimentoAntes, depois: data },
      });

      res.json(alimento);
    } catch (error: any) {
      console.error('Erro ao atualizar alimento:', error);
      res.status(400).json({ message: error.message || 'Erro ao atualizar alimento' });
    }
  });

  // Deletar alimento
  app.delete('/api/alimentos/:id', requireAuth, async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);

      const alimento = await storage.getAlimento(id);
      if (!alimento) {
        return res.status(404).json({ message: 'Alimento no encontrado' });
      }

      const success = await storage.deleteAlimento(id);

      // Registrar no audit log
      await storage.createAuditLog({
        alimentoId: undefined, // Alimento foi deletado
        alimentoCodigo: alimento.codigoProduto,
        alimentoNome: alimento.nome,
        action: 'DELETE',
        userId: req.user.id,
        userName: req.user.nome,
        changes: { alimento },
      });

      res.json({ success });
    } catch (error: any) {
      console.error('Erro ao deletar alimento:', error);
      res.status(500).json({ message: 'Erro ao deletar alimento' });
    }
  });

  // Registrar sada
  app.post('/api/alimentos/:id/saida', requireAuth, async (req: any, res) => {
    try {
      const id = parseInt(req.params.id);
      const { quantidade } = req.body;

      if (!quantidade || quantidade <= 0) {
        return res.status(400).json({ message: 'Quantidade deve ser maior que zero' });
      }

      const alimentoAntes = await storage.getAlimento(id);
      if (!alimentoAntes) {
        return res.status(404).json({ message: 'Alimento no encontrado' });
      }

      if (quantidade > alimentoAntes.quantidade) {
        return res.status(400).json({ message: 'Quantidade maior que o estoque disponvel' });
      }

      const alimento = await storage.registrarSaida(id, quantidade);

      // Registrar no audit log
      await storage.createAuditLog({
        alimentoId: id,
        alimentoCodigo: alimento?.codigoProduto || alimentoAntes.codigoProduto,
        alimentoNome: alimento?.nome || alimentoAntes.nome,
        action: 'SAIDA',
        userId: req.user.id,
        userName: req.user.nome,
        changes: {
          quantidadeSaida: quantidade,
          estoqueAntes: alimentoAntes.quantidade,
          estoqueDepois: alimento?.quantidade || 0,
        },
      });

      res.json(alimento);
    } catch (error: any) {
      console.error('Erro ao registrar sada:', error);
      res.status(500).json({ message: 'Erro ao registrar sada' });
    }
  });

  // Importar mltiplos alimentos
  app.post('/api/alimentos/import', requireAuth, async (req: any, res) => {
    try {
      const { alimentos } = req.body;

      if (!Array.isArray(alimentos)) {
        return res.status(400).json({ message: 'Formato invlido' });
      }

      let imported = 0;
      const errors: string[] = [];

      for (const alimentoData of alimentos) {
        try {
          const data = insertAlimentoSchema.parse(alimentoData);

          // IMPORTANTE: Data de entrada sempre vem do servidor (data atual) na importao
          const alimentoComDataServidor = {
            ...data,
            dataEntrada: new Date().toISOString().split('T')[0],
            dataSaida: undefined,
          };

          const alimento = await storage.createAlimento(alimentoComDataServidor, req.user.id);

          // Registrar no audit log
          await storage.createAuditLog({
            alimentoId: alimento.id,
            alimentoCodigo: alimento.codigoProduto,
            alimentoNome: alimento.nome,
            action: 'CREATE',
            userId: req.user.id,
            userName: req.user.nome,
            changes: { alimento: data, importado: true },
          });

          imported++;
        } catch (error: any) {
          errors.push(`Erro ao importar ${alimentoData.nome}: ${error.message}`);
        }
      }

      res.json({ imported, errors });
    } catch (error: any) {
      console.error('Erro na importao:', error);
      res.status(500).json({ message: 'Erro na importao' });
    }
  });

  // ============ AUDIT LOG ============

  // Listar audit logs
  app.get('/api/audit-log', requireAuth, async (req, res) => {
    try {
      const logs = await storage.getAllAuditLogs();
      res.json(logs);
    } catch (error: any) {
      console.error('Erro ao listar audit logs:', error);
      res.status(500).json({ message: 'Erro ao listar histrico' });
    }
  });

  const httpServer = createServer(app);

  return httpServer;
}

