import { useState } from 'react';
import { useLocation } from 'wouter';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { useToast } from '@/hooks/use-toast';
import { Eye, EyeOff } from 'lucide-react';
import { apiRequest, queryClient } from '@/lib/queryClient';
import { useMutation } from '@tanstack/react-query';
import { loginSchema, insertUserSchema, forgotPasswordSchema, type LoginData, type InsertUser, type ForgotPasswordData } from '@shared/schema';
import logoPrieto from '@assets/LOGO-PRIETO_1761688931089.png';

export default function Login() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [isLogin, setIsLogin] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const [showForgotPassword, setShowForgotPassword] = useState(false);

  // Formulrio de login
  const loginForm = useForm<LoginData>({
    resolver: zodResolver(loginSchema),
    defaultValues: { email: '', password: '' },
  });

  // Formulrio de registro
  const registerForm = useForm<InsertUser>({
    resolver: zodResolver(insertUserSchema),
    defaultValues: { nome: '', email: '', password: '' },
  });

  // Formulrio de recuperao de senha
  const forgotPasswordForm = useForm<ForgotPasswordData>({
    resolver: zodResolver(forgotPasswordSchema),
    defaultValues: { email: '' },
  });

  // Mutation de login
  const loginMutation = useMutation({
    mutationFn: async (data: LoginData) => {
      const response = await apiRequest('POST', '/api/auth/login', data);
      return response;
    },
    onSuccess: () => {
      toast({ title: 'Sucesso', description: 'Login realizado com sucesso!' });
      queryClient.invalidateQueries({ queryKey: ['/api/auth/me'] });
      setLocation('/');
    },
    onError: (error: any) => {
      toast({
        title: 'Erro',
        description: error.message || 'Email ou senha incorretos',
        variant: 'destructive',
      });
    },
  });

  // Mutation de registro
  const registerMutation = useMutation({
    mutationFn: async (data: InsertUser) => {
      const response = await apiRequest('POST', '/api/auth/register', data);
      return response;
    },
    onSuccess: () => {
      toast({ title: 'Sucesso', description: 'Cadastro realizado com sucesso!' });
      queryClient.invalidateQueries({ queryKey: ['/api/auth/me'] });
      setLocation('/');
    },
    onError: (error: any) => {
      toast({
        title: 'Erro',
        description: error.message || 'Este email j est cadastrado',
        variant: 'destructive',
      });
    },
  });

  // Mutation de recuperao de senha
  const forgotPasswordMutation = useMutation({
    mutationFn: async (data: ForgotPasswordData) => {
      const response = await apiRequest('POST', '/api/auth/forgot-password', data);
      return response;
    },
    onSuccess: () => {
      toast({
        title: 'Email enviado',
        description: 'Instrues para recuperao de senha foram enviadas para seu email',
      });
      setShowForgotPassword(false);
    },
    onError: (error: any) => {
      toast({
        title: 'Erro',
        description: error.message || 'Erro ao enviar email de recuperao',
        variant: 'destructive',
      });
    },
  });

  const handleLogin = (data: LoginData) => {
    loginMutation.mutate(data);
  };

  const handleRegister = (data: InsertUser) => {
    registerMutation.mutate(data);
  };

  const handleForgotPassword = (data: ForgotPasswordData) => {
    forgotPasswordMutation.mutate(data);
  };

  if (showForgotPassword) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-background via-background to-secondary/10 flex items-center justify-center p-4">
        <Card className="w-full max-w-md">
          <CardHeader className="space-y-2 text-center">
            <CardTitle className="text-2xl">Recuperar Senha</CardTitle>
            <CardDescription>
              Digite seu email para receber instrues de recuperao
            </CardDescription>
          </CardHeader>
          <CardContent>
            <form onSubmit={forgotPasswordForm.handleSubmit(handleForgotPassword)} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="forgot-email">Email</Label>
                <Input
                  data-testid="input-forgot-email"
                  id="forgot-email"
                  type="email"
                  placeholder="seu@email.com"
                  {...forgotPasswordForm.register('email')}
                />
                {forgotPasswordForm.formState.errors.email && (
                  <p className="text-sm text-destructive">
                    {forgotPasswordForm.formState.errors.email.message}
                  </p>
                )}
              </div>

              <Button
                data-testid="button-send-recovery"
                type="submit"
                className="w-full"
                disabled={forgotPasswordMutation.isPending}
              >
                {forgotPasswordMutation.isPending ? 'Enviando...' : 'Enviar Email'}
              </Button>

              <div className="text-center text-sm">
                <button
                  data-testid="button-back-to-login"
                  type="button"
                  onClick={() => setShowForgotPassword(false)}
                  className="text-primary hover:underline"
                >
                  Voltar ao login
                </button>
              </div>
            </form>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-background via-background to-secondary/10 flex items-center justify-center p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="space-y-2 text-center">
          <div className="flex justify-center mb-4">
            <img 
              src={logoPrieto} 
              alt="Prieto" 
              className="h-20 w-auto"
              data-testid="img-logo-login"
            />
          </div>
          <CardTitle className="text-2xl">
            {isLogin ? 'Entrar no Sistema' : 'Criar Conta'}
          </CardTitle>
          <CardDescription>
            {isLogin
              ? 'Entre com suas credenciais para acessar o sistema'
              : 'Crie sua conta para comear a usar o sistema'}
          </CardDescription>
        </CardHeader>
        <CardContent>
          <form
            onSubmit={isLogin
              ? loginForm.handleSubmit(handleLogin)
              : registerForm.handleSubmit(handleRegister)
            }
            className="space-y-4"
          >
            {!isLogin && (
              <div className="space-y-2">
                <Label htmlFor="nome">Nome Completo</Label>
                <Input
                  data-testid="input-name"
                  id="nome"
                  type="text"
                  placeholder="Seu nome completo"
                  {...registerForm.register('nome')}
                />
                {registerForm.formState.errors.nome && (
                  <p className="text-sm text-destructive">
                    {registerForm.formState.errors.nome.message}
                  </p>
                )}
              </div>
            )}

            <div className="space-y-2">
              <Label htmlFor="email">Email</Label>
              <Input
                data-testid="input-email"
                id="email"
                type="email"
                placeholder="seu@email.com"
                {...(isLogin ? loginForm.register('email') : registerForm.register('email'))}
              />
              {(isLogin ? loginForm.formState.errors.email : registerForm.formState.errors.email) && (
                <p className="text-sm text-destructive">
                  {(isLogin
                    ? loginForm.formState.errors.email?.message
                    : registerForm.formState.errors.email?.message
                  )}
                </p>
              )}
            </div>

            <div className="space-y-2">
              <Label htmlFor="password">Senha</Label>
              <div className="relative">
                <Input
                  data-testid="input-password"
                  id="password"
                  type={showPassword ? 'text' : 'password'}
                  placeholder=""
                  {...(isLogin ? loginForm.register('password') : registerForm.register('password'))}
                  className="pr-10"
                />
                <button
                  data-testid="button-toggle-password"
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute right-3 top-1/2 -translate-y-1/2 text-muted-foreground hover:text-foreground"
                >
                  {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                </button>
              </div>
              {(isLogin ? loginForm.formState.errors.password : registerForm.formState.errors.password) && (
                <p className="text-sm text-destructive">
                  {(isLogin
                    ? loginForm.formState.errors.password?.message
                    : registerForm.formState.errors.password?.message
                  )}
                </p>
              )}
            </div>

            {isLogin && (
              <div className="text-right">
                <button
                  data-testid="button-forgot-password"
                  type="button"
                  onClick={() => setShowForgotPassword(true)}
                  className="text-sm text-primary hover:underline"
                >
                  Esqueci minha senha
                </button>
              </div>
            )}

            <Button
              data-testid="button-submit"
              type="submit"
              className="w-full"
              disabled={isLogin ? loginMutation.isPending : registerMutation.isPending}
            >
              {isLogin
                ? (loginMutation.isPending ? 'Entrando...' : 'Entrar')
                : (registerMutation.isPending ? 'Registrando...' : 'Registrar')}
            </Button>

            <div className="text-center text-sm">
              <button
                data-testid="button-toggle-mode"
                type="button"
                onClick={() => {
                  setIsLogin(!isLogin);
                  loginForm.reset();
                  registerForm.reset();
                }}
                className="text-primary hover:underline"
              >
                {isLogin
                  ? 'No tem conta? Registre-se'
                  : 'J tem conta? Faa login'}
              </button>
            </div>
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

