import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Label } from '@/components/ui/label';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { useToast } from '@/hooks/use-toast';
import { Upload, FileSpreadsheet, CheckCircle2, AlertCircle } from 'lucide-react';
import { useMutation } from '@tanstack/react-query';
import { apiRequest, queryClient } from '@/lib/queryClient';
import * as XLSX from 'xlsx';
import type { InsertAlimento } from '@shared/schema';

interface ImportExcelDialogProps {
  open: boolean;
  onClose: () => void;
}

export function ImportExcelDialog({ open, onClose }: ImportExcelDialogProps) {
  const { toast } = useToast();
  const [file, setFile] = useState<File | null>(null);
  const [preview, setPreview] = useState<any[]>([]);
  const [errors, setErrors] = useState<string[]>([]);

  const importMutation = useMutation({
    mutationFn: async (alimentos: InsertAlimento[]) => {
      const response = await apiRequest('POST', '/api/alimentos/import', { alimentos });
      return response;
    },
    onSuccess: (data: any) => {
      queryClient.invalidateQueries({ queryKey: ['/api/alimentos'] });
      toast({
        title: 'Importao concluda',
        description: `${data.imported} alimentos importados com sucesso!`,
      });
      handleClose();
    },
    onError: (error: any) => {
      toast({
        title: 'Erro na importao',
        description: error.message || 'Erro ao importar alimentos',
        variant: 'destructive',
      });
    },
  });

  const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFile = e.target.files?.[0];
    if (!selectedFile) return;

    setFile(selectedFile);
    setErrors([]);

    try {
      const data = await selectedFile.arrayBuffer();
      const workbook = XLSX.read(data);
      const sheetName = workbook.SheetNames[0];
      const worksheet = workbook.Sheets[sheetName];
      const jsonData = XLSX.utils.sheet_to_json(worksheet);

      // Validar e processar dados
      const processedData: InsertAlimento[] = [];
      const validationErrors: string[] = [];

      jsonData.forEach((row: any, index: number) => {
        try {
          let codigoProduto = String(row['Cdigo Produto'] || row['codigoProduto'] || row['Z06_COD'] || row['Codigo'] || '').trim();
          let nome = String(row['Nome'] || row['nome'] || row['Z06_DESC'] || row['Descricao'] || row['Descrio'] || '').trim();
          let temperatura = String(row['Temperatura'] || row['temperatura'] || row['Z06_ARMA'] || '').trim();
          let lote = String(row['Lote'] || row['lote'] || 'LOTE-01').trim();

          let dataFabricacao = row['Data Fabricao'] || row['dataFabricacao'] || row['Data Fabricacao'] || row['Data de Fabricacao'];
          if (typeof dataFabricacao === 'number') {
            const date = new Date((dataFabricacao - 25569) * 86400 * 1000);
            dataFabricacao = date.toISOString().split('T')[0];
          }

          let dataValidade = row['Data Validade'] || row['dataValidade'] || row['Data de Validade'];
          if (typeof dataValidade === 'number') {
            const date = new Date((dataValidade - 25569) * 86400 * 1000);
            dataValidade = date.toISOString().split('T')[0];
          }

          const shelfLife = Number(row['Shelf Life (dias)'] || row['shelfLife'] || row['Z06_PRAZO'] || row['Shelf Life'] || 365);

          if (!dataFabricacao) {
            dataFabricacao = new Date().toISOString().split('T')[0];
          }

          if (!dataValidade && dataFabricacao && shelfLife) {
            const fab = new Date(dataFabricacao);
            fab.setDate(fab.getDate() + Number(shelfLife));
            dataValidade = fab.toISOString().split('T')[0];
          }

          const quantidade = Number(row['Quantidade'] || row['quantidade'] || row['Qtd'] || 0);
          const pesoPorCaixa = row['Peso por Caixa (kg)'] || row['pesoPorCaixa'] || row['Z06_TRCX'] || row['Peso Caixa'] 
            ? Number(row['Peso por Caixa (kg)'] || row['pesoPorCaixa'] || row['Z06_TRCX'] || row['Peso Caixa']) 
            : undefined;

          const unidade = String(row['Unidade'] || row['unidade'] || 'kg').toLowerCase();

          const alimento: InsertAlimento = {
            codigoProduto,
            nome,
            unidade: (unidade === 'caixa' || unidade === 'cx') ? 'caixa' : 'kg',
            lote,
            dataFabricacao: String(dataFabricacao),
            dataValidade: String(dataValidade),
            quantidade,
            pesoPorCaixa,
            temperatura,
            shelfLife,
            alertasConfig: {
              contarAPartirFabricacaoDias: 10,
              avisoQuandoUmTercoValidade: true,
              popUpNotificacoes: true,
            },
          };

          if (!alimento.codigoProduto || !alimento.nome) {
            validationErrors.push(`Linha ${index + 2}: Faltam campos obrigatrios (Cdigo ou Nome)`);
          } else {
            processedData.push(alimento);
          }
        } catch (error) {
          validationErrors.push(`Linha ${index + 2}: Erro ao processar dados - ${error}`);
        }
      });

      setPreview(processedData.slice(0, 5)); // Mostrar apenas primeiros 5 para preview
      setErrors(validationErrors);

      if (validationErrors.length > 0) {
        toast({
          title: 'Avisos na importao',
          description: `${validationErrors.length} linhas com problemas`,
          variant: 'default',
        });
      }
    } catch (error) {
      toast({
        title: 'Erro ao ler arquivo',
        description: 'Verifique se o arquivo  um Excel vlido',
        variant: 'destructive',
      });
    }
  };

  const handleImport = () => {
    if (preview.length > 0) {
      importMutation.mutate(preview);
    }
  };

  const handleClose = () => {
    setFile(null);
    setPreview([]);
    setErrors([]);
    onClose();
  };

  return (
    <Dialog open={open} onOpenChange={handleClose}>
      <DialogContent className="max-w-3xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Importar Alimentos via Excel</DialogTitle>
          <DialogDescription>
            Faa upload de um arquivo Excel (.xlsx, .xls) com os dados dos alimentos.
            <br />
            <span className="text-sm font-medium mt-2 block">
              Auto-preenchimento: Se informar Data de Fabricao e Shelf Life, a Data de Validade ser calculada automaticamente.
            </span>
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          {/* Upload Area */}
          <div className="border-2 border-dashed border-muted-foreground/25 rounded-lg p-8 text-center hover-elevate">
            <div className="flex flex-col items-center gap-4">
              <FileSpreadsheet className="h-12 w-12 text-muted-foreground" />
              <div>
                <Label htmlFor="file-upload" className="cursor-pointer">
                  <div className="text-base font-medium text-primary hover:underline">
                    Clique para selecionar um arquivo
                  </div>
                  <div className="text-sm text-muted-foreground mt-1">
                    Aceita arquivos .xlsx e .xls
                  </div>
                </Label>
                <input
                  data-testid="input-file-upload"
                  id="file-upload"
                  type="file"
                  accept=".xlsx,.xls"
                  onChange={handleFileChange}
                  className="hidden"
                />
              </div>
              {file && (
                <div className="flex items-center gap-2 text-sm">
                  <CheckCircle2 className="h-4 w-4 text-green-600" />
                  <span className="font-medium">{file.name}</span>
                </div>
              )}
            </div>
          </div>

          {/* Erros */}
          {errors.length > 0 && (
            <div className="rounded-lg bg-destructive/10 border border-destructive/20 p-4">
              <div className="flex items-start gap-2">
                <AlertCircle className="h-5 w-5 text-destructive mt-0.5" />
                <div className="flex-1">
                  <h4 className="font-semibold text-destructive mb-2">Problemas encontrados:</h4>
                  <ul className="text-sm space-y-1">
                    {errors.slice(0, 5).map((error, i) => (
                      <li key={i}> {error}</li>
                    ))}
                    {errors.length > 5 && (
                      <li className="text-muted-foreground">
                        ... e mais {errors.length - 5} problemas
                      </li>
                    )}
                  </ul>
                </div>
              </div>
            </div>
          )}

          {/* Preview */}
          {preview.length > 0 && (
            <div>
              <h4 className="font-semibold mb-2">Preview ({preview.length} alimentos):</h4>
              <div className="border rounded-lg overflow-hidden">
                <div className="overflow-x-auto">
                  <table className="w-full text-sm">
                    <thead className="bg-muted">
                      <tr>
                        <th className="px-3 py-2 text-left">Cdigo</th>
                        <th className="px-3 py-2 text-left">Nome</th>
                        <th className="px-3 py-2 text-left">Lote</th>
                        <th className="px-3 py-2 text-left">Quantidade</th>
                        <th className="px-3 py-2 text-left">Validade</th>
                      </tr>
                    </thead>
                    <tbody>
                      {preview.map((item, i) => (
                        <tr key={i} className="border-t">
                          <td className="px-3 py-2 font-mono text-xs">{item.codigoProduto}</td>
                          <td className="px-3 py-2">{item.nome}</td>
                          <td className="px-3 py-2 font-mono text-xs">{item.lote}</td>
                          <td className="px-3 py-2">
                            {item.quantidade} {item.unidade}
                          </td>
                          <td className="px-3 py-2 font-mono text-xs">{item.dataValidade}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          )}

          {/* Botes */}
          <div className="flex justify-end gap-3 pt-4">
            <Button data-testid="button-cancel-import" variant="outline" onClick={handleClose}>
              Cancelar
            </Button>
            <Button
              data-testid="button-confirm-import"
              onClick={handleImport}
              disabled={preview.length === 0 || importMutation.isPending}
            >
              {importMutation.isPending ? 'Importando...' : `Importar ${preview.length} alimentos`}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

scripts/setup-supabase-client.ts.content:
import { createClient } from '@supabase/supabase-js';

const supabaseUrl = process.env.SUPABASE_URL;
const supabaseKey = process.env.SUPABASE_ANON_KEY;

if (!supabaseUrl || !supabaseKey) {
  console.error('SUPABASE_URL e SUPABASE_ANON_KEY devem estar configurados');
  process.exit(1);
}

const supabase = createClient(supabaseUrl, supabaseKey);

const schema = `
-- Tabela de usurios
CREATE TABLE IF NOT EXISTS users (
  id VARCHAR PRIMARY KEY DEFAULT gen_random_uuid(),
  nome TEXT NOT NULL,
  email TEXT NOT NULL UNIQUE,
  password TEXT NOT NULL,
  reset_token TEXT,
  reset_token_expiry TIMESTAMP,
  created_at TIMESTAMP DEFAULT NOW() NOT NULL
);

-- Tabela de modelos de produtos
CREATE TABLE IF NOT EXISTS modelos_produtos (
  id SERIAL PRIMARY KEY,
  codigo_produto TEXT NOT NULL UNIQUE,
  descricao TEXT NOT NULL,
  temperatura TEXT NOT NULL,
  shelf_life INTEGER NOT NULL,
  gtin TEXT,
  peso_embalagem REAL,
  peso_por_caixa REAL,
  empresa TEXT,
  peso_liquido REAL,
  tipo_peso TEXT,
  quantidade_por_caixa INTEGER,
  unidade_padrao TEXT NOT NULL DEFAULT 'kg',
  created_at TIMESTAMP DEFAULT NOW() NOT NULL
);

-- Tabela de alimentos
CREATE TABLE IF NOT EXISTS alimentos (
  id SERIAL PRIMARY KEY,
  codigo_produto TEXT NOT NULL,
  nome TEXT NOT NULL,
  unidade TEXT NOT NULL,
  lote TEXT NOT NULL,
  data_fabricacao TEXT NOT NULL,
  data_validade TEXT NOT NULL,
  quantidade REAL NOT NULL DEFAULT 0,
  peso_por_caixa REAL,
  temperatura TEXT NOT NULL,
  shelf_life INTEGER NOT NULL,
  data_entrada TEXT NOT NULL,
  data_saida TEXT,
  alertas_config JSONB NOT NULL,
  cadastrado_por VARCHAR NOT NULL REFERENCES users(id),
  created_at TIMESTAMP DEFAULT NOW() NOT NULL,
  updated_at TIMESTAMP DEFAULT NOW() NOT NULL
);

-- Tabela de auditoria
CREATE TABLE IF NOT EXISTS audit_log (
  id SERIAL PRIMARY KEY,
  alimento_id INTEGER,
  alimento_codigo TEXT,
  alimento_nome TEXT,
  action TEXT NOT NULL,
  user_id VARCHAR NOT NULL REFERENCES users(id),
  user_name TEXT NOT NULL,
  changes JSONB,
  timestamp TIMESTAMP DEFAULT NOW() NOT NULL
);
`;

async function setupDatabase() {
  try {
    console.log('Conectando ao Supabase via cliente JS...');

    // Executar cada comando CREATE TABLE separadamente
    const statements = schema.split(';').filter(s => s.trim().length > 0);

    for (const statement of statements) {
      const { data, error } = await supabase.rpc('exec_sql', { sql: statement + ';' });
      if (error) {
        console.error('Erro ao executar:', statement.substring(0, 50) + '...');
        console.error('Erro:', error);
      }
    }

    console.log(' Setup concludo!');

  } catch (error) {
    console.error(' Erro:', error);
    console.log('\n Voc precisa criar as tabelas manualmente no Supabase SQL Editor.');
    console.log('\n Use este SQL no Supabase SQL Editor:\n');
    console.log(schema);
  }
}

setupDatabase();

