import { useEffect, useState } from 'react';
import { Bell, AlertTriangle, X } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import type { AlimentoComputado } from '@shared/schema';

interface NotificationsPanelProps {
  alimentos: AlimentoComputado[];
}

interface Notification {
  id: string;
  type: 'warning' | 'danger' | 'expired';
  message: string;
  alimento: AlimentoComputado;
}

export function NotificationsPanel({ alimentos }: NotificationsPanelProps) {
  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [isOpen, setIsOpen] = useState(false);
  const [dismissed, setDismissed] = useState<Set<string>>(new Set());

  useEffect(() => {
    const newNotifications: Notification[] = [];
    const today = new Date();

    alimentos.forEach((alimento) => {
      const notifId = `${alimento.id}-${alimento.dataValidade}`;

      if (dismissed.has(notifId)) {
        return;
      }

      const validade = new Date(alimento.dataValidade);
      const fabricacao = new Date(alimento.dataFabricacao);
      const diasTotais = Math.floor((validade.getTime() - fabricacao.getTime()) / (1000 * 60 * 60 * 24));
      const umTerco = Math.floor(diasTotais / 3);
      const diasRestantes = alimento.diasRestantes;

      if (alimento.status === 'VENCIDO') {
        newNotifications.push({
          id: notifId,
          type: 'expired',
          message: `${alimento.nome} (Lote ${alimento.lote}) est VENCIDO! Por favor, dar baixa no estoque.`,
          alimento,
        });
      } else if (diasRestantes <= 7 && diasRestantes > 0) {
        newNotifications.push({
          id: notifId,
          type: 'danger',
          message: `${alimento.nome} (Lote ${alimento.lote}) vence em ${diasRestantes} dia${diasRestantes > 1 ? 's' : ''}!`,
          alimento,
        });
      } else if (diasRestantes <= umTerco && diasRestantes > 7 && alimento.alertasConfig.avisoQuandoUmTercoValidade) {
        newNotifications.push({
          id: notifId,
          type: 'warning',
          message: `${alimento.nome} (Lote ${alimento.lote}) atingiu 1/3 da validade (${diasRestantes} dias restantes).`,
          alimento,
        });
      }
    });

    setNotifications(newNotifications);

    if (newNotifications.length > 0) {
      setIsOpen(true);
    }
  }, [alimentos, dismissed]);

  const dismissNotification = (id: string) => {
    setDismissed(prev => new Set(Array.from(prev).concat(id)));
  };

  const unreadCount = notifications.length;

  if (unreadCount === 0) {
    return null;
  }

  return (
    <div className="fixed bottom-4 right-4 z-50">
      {!isOpen && (
        <Button
          data-testid="button-notifications"
          onClick={() => setIsOpen(true)}
          variant="default"
          size="icon"
          className="h-12 w-12 rounded-full shadow-lg relative"
        >
          <Bell className="h-5 w-5" />
          {unreadCount > 0 && (
            <Badge
              variant="destructive"
              className="absolute -top-1 -right-1 h-6 w-6 rounded-full p-0 flex items-center justify-center"
            >
              {unreadCount}
            </Badge>
          )}
        </Button>
      )}

      {isOpen && (
        <Card className="w-96 max-h-96 overflow-y-auto shadow-xl">
          <div className="sticky top-0 bg-card border-b p-4 flex items-center justify-between">
            <div className="flex items-center gap-2">
              <Bell className="h-5 w-5" />
              <h3 className="font-semibold">Notificaes</h3>
              <Badge variant="secondary">{unreadCount}</Badge>
            </div>
            <Button
              data-testid="button-close-notifications"
              variant="ghost"
              size="icon"
              onClick={() => setIsOpen(false)}
              className="h-8 w-8"
            >
              <X className="h-4 w-4" />
            </Button>
          </div>

          <div className="p-2">
            {notifications.length === 0 ? (
              <div className="text-center py-8 text-muted-foreground">
                Nenhuma notificao
              </div>
            ) : (
              <div className="space-y-2">
                {notifications.map((notif) => (
                  <div
                    key={notif.id}
                    data-testid={`notification-${notif.type}`}
                    className={`p-3 rounded-md border ${
                      notif.type === 'expired'
                        ? 'bg-destructive/10 border-destructive'
                        : notif.type === 'danger'
                        ? 'bg-orange-500/10 border-orange-500'
                        : 'bg-yellow-500/10 border-yellow-500'
                    }`}
                  >
                    <div className="flex items-start gap-2">
                      <AlertTriangle
                        className={`h-5 w-5 mt-0.5 ${
                          notif.type === 'expired'
                            ? 'text-destructive'
                            : notif.type === 'danger'
                            ? 'text-orange-500'
                            : 'text-yellow-600'
                        }`}
                      />
                      <div className="flex-1">
                        <p className="text-sm font-medium">{notif.message}</p>
                        <p className="text-xs text-muted-foreground mt-1">
                          Validade: {notif.alimento.dataValidade}
                        </p>
                      </div>
                      <Button
                        data-testid={`button-dismiss-${notif.id}`}
                        variant="ghost"
                        size="icon"
                        onClick={() => dismissNotification(notif.id)}
                        className="h-6 w-6"
                      >
                        <X className="h-3 w-3" />
                      </Button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </Card>
      )}
    </div>
  );
}

client/src/pages/reset-password.tsx.content:
import { useState, useEffect } from 'react';
import { useLocation } from 'wouter';
import { useToast } from '@/hooks/use-toast';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { apiRequest } from '@/lib/queryClient';
import { Lock, CheckCircle2 } from 'lucide-react';
import logoPrieto from '@assets/LOGO-PRIETO_1761688931089.png';

export default function ResetPassword() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [token, setToken] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isSuccess, setIsSuccess] = useState(false);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const tokenParam = params.get('token');
    if (tokenParam) {
      setToken(tokenParam);
    } else {
      toast({
        title: 'Erro',
        description: 'Token de recuperao no encontrado',
        variant: 'destructive',
      });
      setTimeout(() => setLocation('/login'), 2000);
    }
  }, []);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (newPassword !== confirmPassword) {
      toast({
        title: 'Erro',
        description: 'As senhas no coincidem',
        variant: 'destructive',
      });
      return;
    }

    if (newPassword.length < 6) {
      toast({
        title: 'Erro',
        description: 'A senha deve ter pelo menos 6 caracteres',
        variant: 'destructive',
      });
      return;
    }

    setIsLoading(true);

    try {
      await apiRequest('POST', '/api/auth/reset-password', {
        token,
        newPassword,
      });

      setIsSuccess(true);
      toast({
        title: 'Sucesso!',
        description: 'Senha redefinida com sucesso!',
      });

      setTimeout(() => {
        setLocation('/login');
      }, 2000);
    } catch (error: any) {
      toast({
        title: 'Erro',
        description: error.message || 'Erro ao redefinir senha',
        variant: 'destructive',
      });
    } finally {
      setIsLoading(false);
    }
  };

  if (isSuccess) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-background p-4">
        <Card className="w-full max-w-md">
          <CardHeader className="text-center">
            <div className="flex justify-center mb-4">
              <img src={logoPrieto} alt="Prieto" className="h-16 w-auto" />
            </div>
            <div className="flex justify-center mb-4">
              <CheckCircle2 className="h-16 w-16 text-green-600" />
            </div>
            <CardTitle>Senha Redefinida!</CardTitle>
            <CardDescription>
              Sua senha foi redefinida com sucesso. Voc ser redirecionado para a pgina de login.
            </CardDescription>
          </CardHeader>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-background p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center">
          <div className="flex justify-center mb-4">
            <img src={logoPrieto} alt="Prieto" className="h-16 w-auto" data-testid="img-logo" />
          </div>
          <CardTitle>Redefinir Senha</CardTitle>
          <CardDescription>
            Digite sua nova senha abaixo
          </CardDescription>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="newPassword">Nova Senha</Label>
              <div className="relative">
                <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                <Input
                  id="newPassword"
                  data-testid="input-new-password"
                  type="password"
                  placeholder="Digite sua nova senha"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  className="pl-10"
                  required
                  minLength={6}
                />
              </div>
            </div>

            <div className="space-y-2">
              <Label htmlFor="confirmPassword">Confirmar Nova Senha</Label>
              <div className="relative">
                <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                <Input
                  id="confirmPassword"
                  data-testid="input-confirm-password"
                  type="password"
                  placeholder="Confirme sua nova senha"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  className="pl-10"
                  required
                  minLength={6}
                />
              </div>
            </div>

            <Button
              data-testid="button-reset-password"
              type="submit"
              className="w-full"
              disabled={isLoading}
            >
              {isLoading ? 'Redefinindo...' : 'Redefinir Senha'}
            </Button>

            <Button
              data-testid="button-back-to-login"
              type="button"
              variant="ghost"
              className="w-full"
              onClick={() => setLocation('/login')}
            >
              Voltar para o login
            </Button>
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

