import { storage } from '../storage';
import bcrypt from 'bcryptjs';

async function createTestUser() {
  try {
    console.log('Criando usurio de teste...');

    const testEmail = 'teste@prieto.com';
    const existingUser = await storage.getUserByEmail(testEmail);

    if (existingUser) {
      console.log(' Usurio de teste j existe:', testEmail);
      console.log('  ID:', existingUser.id);
      console.log('  Nome:', existingUser.nome);
      return;
    }

    const hashedPassword = await bcrypt.hash('teste123', 10);

    const user = await storage.createUser({
      nome: 'Usurio Teste',
      email: testEmail,
      password: hashedPassword,
    });

    console.log(' Usurio de teste criado com sucesso!');
    console.log('  Email:', user.email);
    console.log('  Senha: teste123');
    console.log('  ID:', user.id);
    console.log('\nEste usurio est agora disponvel nas tabelas do Supabase.');
  } catch (error) {
    console.error(' Erro ao criar usurio de teste:', error);
    process.exit(1);
  }
}

createTestUser();
